targets.expr:
(
  sum by(deployment_environment_name, service_namespace, service_name) (
    rate(
      http_server_request_duration_seconds_count{
        deployment_environment_name=~"$deployment_environment_name",
        service_namespace=~"$service_namespace",
        service_name="$service_name",
        http_response_status_code=~"5.."
      }[$__rate_interval]
    )
  ) * 100
)
/
(
  sum by(deployment_environment_name, service_namespace, service_name) (
    rate(
      http_server_request_duration_seconds_count{
        deployment_environment_name=~"$deployment_environment_name",
        service_namespace=~"$service_namespace",
        service_name="$service_name"
      }[$__rate_interval]
    )
  )
)
or
(
  0
  *
  sum by(deployment_environment_name, service_namespace, service_name) (
    rate(
      http_server_request_duration_seconds_count{
        deployment_environment_name=~"$deployment_environment_name",
        service_namespace=~"$service_namespace",
        service_name="$service_name"
      }[$__rate_interval]
    )
  )
)

(
  sum without (rpc_grpc_status_code, instance) (
    rate(
      rpc_server_duration_milliseconds_count{
        deployment_environment_name=~"$deployment_environment_name",
        service_namespace=~"$service_namespace",
        service_name="$service_name",
        rpc_grpc_status_code!="0"
      }[$__rate_interval]
    )
  ) * 100
)
/
(
  sum without (rpc_grpc_status_code, instance) (
    rate(
      rpc_server_duration_milliseconds_count{
        deployment_environment_name=~"$deployment_environment_name",
        service_namespace=~"$service_namespace",
        service_name="$service_name"
      }[$__rate_interval]
    )
  )
)
or 
(
    0
    *
    sum without (rpc_grpc_status_code, instance) (
    rate(
      rpc_server_duration_milliseconds_count{
        deployment_environment_name=~"$deployment_environment_name",
        service_namespace=~"$service_namespace",
        service_name="$service_name"
      }[$__rate_interval]
    )
  )
)

sum by (outbound_service) (
  label_join(
    rate(
      http_client_request_duration_seconds_count{
        deployment_environment_name=~"$deployment_environment_name",
        service_namespace=~"$service_namespace",
        service_name="$service_name"
      }[$__rate_interval]
    ),
    "outbound_service",
    " ",
    "server_address",
    "http_request_method",
    "url_template"
  )
)

histogram_quantile(
  0.99,
  sum by (le, outbound_service) (
    label_join(
      rate(
        http_client_request_duration_seconds_bucket{
          deployment_environment_name=~"$deployment_environment_name",
          service_namespace=~"$service_namespace",
          service_name="$service_name"
        }[5m]
      ),
      "outbound_service",
      " ",
      "server_address",
      "http_request_method",
      "url_template"
    )
  )
)

sum by (database) (
  label_join(
    rate(
      db_client_operation_duration_seconds_count{
        deployment_environment_name=~"$deployment_environment_name",
        service_namespace=~"$service_namespace",
        service_name="$service_name"
      }[$__rate_interval]
    ),
    "database",
    "/",
    "server_address",
    "db_namespace"
  )
)

histogram_quantile(
  0.99,
  sum by (le, database) (
    label_join(
      rate(
        db_client_operation_duration_seconds_bucket{
          deployment_environment_name=~"$deployment_environment_name",
          service_namespace=~"$service_namespace",
          service_name="$service_name"
        }[5m]
      ),
      "database",
      "/",
      "server_address",
      "db_namespace"
    )
  )
)
